import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import api from '../services/api';
import './Dashboard.css';

const AssignComplaints = () => {
    const [user, setUser] = useState(null);
    const [unassignedComplaints, setUnassignedComplaints] = useState([]);
    const [agents, setAgents] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (!userData) {
            navigate('/login');
            return;
        }

        const parsedUser = JSON.parse(userData);
        if (parsedUser.role !== 'MANAGER') {
            navigate('/login');
            return;
        }

        setUser(parsedUser);
        fetchData(parsedUser.userId);
    }, [navigate]);

    const fetchData = async (managerId) => {
        try {
            const [complaintsResponse, agentsResponse] = await Promise.all([
                api.get('/complaint/unassigned'),
                api.get(`/manager/${managerId}/agents`)
            ]);
            setUnassignedComplaints(complaintsResponse.data);
            setAgents(agentsResponse.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        } finally {
            setLoading(false);
        }
    };

    const assignComplaint = async (complaintId, agentId) => {
        try {
            await api.post('/complaint/assign', {
                complaintId,
                agentId,
                managerId: user.userId
            });
            fetchData(user.userId);
        } catch (error) {
            console.error('Error assigning complaint:', error);
        }
    };

    const getSpecializedAgents = (complaintType) => {
        const typeMapping = {
            'billing': 'BILLING',
            'service': 'SERVICE', 
            'technical': 'TECHNICAL',
            'account': 'ACCOUNT'
        };

        const specialization = typeMapping[complaintType.toLowerCase()];
        return agents.filter(agent => agent.agentSpecialization === specialization);
    };

    const autoAssign = async (complaintId, complaintType) => {
        const specializedAgents = getSpecializedAgents(complaintType);
        if (specializedAgents.length > 0) {
            // Assign to agent with least workload (simplified)
            const selectedAgent = specializedAgents[0];
            await assignComplaint(complaintId, selectedAgent.userId);
        }
    };

    if (loading) return <div className="loading">Loading...</div>;

    return (
        <div className="dashboard-container">
            <Sidebar userRole="MANAGER" />
            <div className="main-content">
                <div className="dashboard-header">
                    <h1>Assign Complaints</h1>
                    <p>Manage complaint assignments to agents</p>
                </div>

                <div className="stats-grid">
                    <div className="stat-card">
                        <h3>Unassigned Complaints</h3>
                        <div className="stat-number">{unassignedComplaints.length}</div>
                    </div>
                    <div className="stat-card">
                        <h3>Available Agents</h3>
                        <div className="stat-number">{agents.length}</div>
                    </div>
                </div>

                <div className="complaints-section">
                    <h2>Unassigned Complaints</h2>
                    <div className="complaints-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>Tracking ID</th>
                                    <th>Customer</th>
                                    <th>Type</th>
                                    <th>Description</th>
                                    <th>Submitted Date</th>
                                    <th>Specialized Agents</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {unassignedComplaints.map(complaint => {
                                    const specializedAgents = getSpecializedAgents(complaint.complaintType);
                                    return (
                                        <tr key={complaint.complaintId}>
                                            <td>{complaint.trackingId}</td>
                                            <td>{complaint.user.username}</td>
                                            <td>{complaint.complaintType}</td>
                                            <td className="description-cell">
                                                {complaint.complaintDescription.substring(0, 50)}...
                                            </td>
                                            <td>{complaint.submittedDate}</td>
                                            <td>
                                                <select 
                                                    className="agent-select"
                                                    onChange={(e) => {
                                                        if (e.target.value) {
                                                            assignComplaint(complaint.complaintId, e.target.value);
                                                        }
                                                    }}
                                                    defaultValue=""
                                                >
                                                    <option value="">Select Agent</option>
                                                    {specializedAgents.map(agent => (
                                                        <option key={agent.userId} value={agent.userId}>
                                                            {agent.username} ({agent.agentSpecialization})
                                                        </option>
                                                    ))}
                                                </select>
                                            </td>
                                            <td>
                                                <div className="action-buttons">
                                                    <button 
                                                        className="btn btn-primary"
                                                        onClick={() => autoAssign(complaint.complaintId, complaint.complaintType)}
                                                        disabled={specializedAgents.length === 0}
                                                    >
                                                        Auto Assign
                                                    </button>
                                                    <button 
                                                        className="btn btn-info"
                                                        onClick={() => navigate(`/complaint-details/${complaint.complaintId}`)}
                                                    >
                                                        View Details
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                        {unassignedComplaints.length === 0 && (
                            <div className="no-data">No unassigned complaints.</div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AssignComplaints;
