import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import api from '../services/api';
import '../styles/Dashboard.css';

const AgentDashboard = () => {
    const [user, setUser] = useState(null);
    const [assignedComplaints, setAssignedComplaints] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (!userData) {
            navigate('/login');
            return;
        }

        const parsedUser = JSON.parse(userData);
        if (parsedUser.role !== 'AGENT') {
            navigate('/login');
            return;
        }

        setUser(parsedUser);
        fetchAssignedComplaints(parsedUser.userId);
    }, [navigate]);

    const fetchAssignedComplaints = async (agentId) => {
        try {
            const response = await api.get(`/complaint/agent/${agentId}`);
            setAssignedComplaints(response.data);
        } catch (error) {
            console.error('Error fetching assigned complaints:', error);
        } finally {
            setLoading(false);
        }
    };

    const updateComplaintStatus = async (complaintId, status) => {
        try {
            await api.post('/complaint/update-status', {
                complaintId,
                status
            });
            fetchAssignedComplaints(user.userId);
        } catch (error) {
            console.error('Error updating status:', error);
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'Assigned': return '#ffc107';
            case 'In Progress': return '#17a2b8';
            case 'Resolved': return '#28a745';
            default: return '#6c757d';
        }
    };

    if (loading) return <div className="loading">Loading...</div>;

    return (
        <div className="dashboard-container">
            <Sidebar userRole="AGENT" />
            <div className="main-content">
                <div className="dashboard-header">
                    <h1>Agent Dashboard</h1>
                    <p>Welcome back, {user?.username}!</p>
                    <p>Specialization: <strong>{user?.agentSpecialization}</strong></p>
                </div>

                <div className="stats-grid">
                    <div className="stat-card">
                        <h3>My Assigned Complaints</h3>
                        <div className="stat-number">{assignedComplaints.length}</div>
                    </div>
                    <div className="stat-card">
                        <h3>In Progress</h3>
                        <div className="stat-number">
                            {assignedComplaints.filter(c => c.status === 'In Progress').length}
                        </div>
                    </div>
                    <div className="stat-card">
                        <h3>Resolved Today</h3>
                        <div className="stat-number">
                            {assignedComplaints.filter(c => 
                                c.status === 'Resolved' && 
                                c.resolvedDate === new Date().toISOString().split('T')[0]
                            ).length}
                        </div>
                    </div>
                </div>

                <div className="complaints-section">
                    <h2>My Assigned Complaints</h2>
                    <div className="complaints-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>Tracking ID</th>
                                    <th>Customer</th>
                                    <th>Type</th>
                                    <th>Description</th>
                                    <th>Status</th>
                                    <th>Assigned Date</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {assignedComplaints.map(complaint => (
                                    <tr key={complaint.complaintId}>
                                        <td>{complaint.trackingId}</td>
                                        <td>{complaint.user.username}</td>
                                        <td>{complaint.complaintType}</td>
                                        <td className="description-cell">
                                            {complaint.complaintDescription.substring(0, 50)}...
                                        </td>
                                        <td>
                                            <span 
                                                className="status-badge"
                                                style={{ backgroundColor: getStatusColor(complaint.status) }}
                                            >
                                                {complaint.status}
                                            </span>
                                        </td>
                                        <td>{complaint.assignmentDate}</td>
                                        <td>
                                            <div className="action-buttons">
                                                {complaint.status === 'Assigned' && (
                                                    <button 
                                                        className="btn btn-primary"
                                                        onClick={() => updateComplaintStatus(complaint.complaintId, 'In Progress')}
                                                    >
                                                        Start Work
                                                    </button>
                                                )}
                                                {complaint.status === 'In Progress' && (
                                                    <button 
                                                        className="btn btn-success"
                                                        onClick={() => updateComplaintStatus(complaint.complaintId, 'Resolved')}
                                                    >
                                                        Mark Resolved
                                                    </button>
                                                )}
                                                <button 
                                                    className="btn btn-info"
                                                    onClick={() => navigate(`/complaint-details/${complaint.complaintId}`)}
                                                >
                                                    View Details
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        {assignedComplaints.length === 0 && (
                            <div className="no-data">No complaints assigned yet.</div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AgentDashboard;
