import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const AgentDashboard = () => {
    const [agent, setAgent] = useState(null);
    const [complaints, setComplaints] = useState([]);
    const [activeComplaints, setActiveComplaints] = useState([]);
    const [stats, setStats] = useState({});
    const [selectedComplaint, setSelectedComplaint] = useState(null);
    const [resolutionNotes, setResolutionNotes] = useState('');

    useEffect(() => {
        // Get agent info from localStorage or session
        const agentData = JSON.parse(localStorage.getItem('agentData'));
        if (agentData) {
            setAgent(agentData);
            fetchComplaints(agentData.agentId);
            fetchStats(agentData.agentId);
        }
    }, []);

    const fetchComplaints = async (agentId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/agent/${agentId}/complaints`);
            const data = await response.json();
            setComplaints(data);

            const activeResponse = await fetch(`http://localhost:8080/api/agent/${agentId}/complaints/active`);
            const activeData = await activeResponse.json();
            setActiveComplaints(activeData);
        } catch (error) {
            console.error('Error fetching complaints:', error);
        }
    };

    const fetchStats = async (agentId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/agent/${agentId}/dashboard/stats`);
            const data = await response.json();
            setStats(data);
        } catch (error) {
            console.error('Error fetching stats:', error);
        }
    };

    const startWorking = async (complaintId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/agent/complaint/${complaintId}/start`, {
                method: 'POST'
            });
            if (response.ok) {
                alert('Started working on complaint');
                fetchComplaints(agent.agentId);
                fetchStats(agent.agentId);
            }
        } catch (error) {
            console.error('Error starting work:', error);
        }
    };

    const solveComplaint = async (complaintId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/agent/complaint/${complaintId}/solve`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ resolutionNotes })
            });
            if (response.ok) {
                alert('Complaint marked as solved');
                setSelectedComplaint(null);
                setResolutionNotes('');
                fetchComplaints(agent.agentId);
                fetchStats(agent.agentId);
            }
        } catch (error) {
            console.error('Error solving complaint:', error);
        }
    };

    if (!agent) {
        return <div>Please login as an agent</div>;
    }

    return (
        <div className="dashboard-container">
            <h1>Agent Dashboard - {agent.agentName}</h1>
            <p>Department: {agent.department}</p>

            <div className="stats-grid">
                <div className="stat-card">
                    <h3>Total Assigned</h3>
                    <p>{stats.totalAssigned || 0}</p>
                </div>
                <div className="stat-card">
                    <h3>Active</h3>
                    <p>{stats.active || 0}</p>
                </div>
                <div className="stat-card">
                    <h3>Solved</h3>
                    <p>{stats.solved || 0}</p>
                </div>
                <div className="stat-card">
                    <h3>Resolved</h3>
                    <p>{stats.resolved || 0}</p>
                </div>
            </div>

            <div className="complaints-section">
                <h2>My Assigned Complaints</h2>
                <table className="complaints-table">
                    <thead>
                        <tr>
                            <th>Tracking ID</th>
                            <th>Type</th>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Submitted Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {complaints.map(complaint => (
                            <tr key={complaint.complaintId}>
                                <td>{complaint.trackingId}</td>
                                <td>{complaint.complaintType}</td>
                                <td>{complaint.complaintDescription.substring(0, 50)}...</td>
                                <td>{complaint.status}</td>
                                <td>{complaint.submittedDate}</td>
                                <td>
                                    {complaint.status === 'ASSIGNED' && (
                                        <button onClick={() => startWorking(complaint.complaintId)}>
                                            Start Working
                                        </button>
                                    )}
                                    {complaint.status === 'IN_PROGRESS' && (
                                        <button onClick={() => setSelectedComplaint(complaint)}>
                                            Mark as Solved
                                        </button>
                                    )}
                                    {complaint.status === 'SOLVED' && (
                                        <span>Waiting for Manager Approval</span>
                                    )}
                                    {complaint.status === 'RESOLVED' && (
                                        <span>Completed</span>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {selectedComplaint && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Resolve Complaint: {selectedComplaint.trackingId}</h3>
                        <p><strong>Description:</strong> {selectedComplaint.complaintDescription}</p>
                        <textarea
                            placeholder="Enter resolution notes..."
                            value={resolutionNotes}
                            onChange={(e) => setResolutionNotes(e.target.value)}
                            rows="4"
                            cols="50"
                        />
                        <div>
                            <button onClick={() => solveComplaint(selectedComplaint.complaintId)}>
                                Mark as Solved
                            </button>
                            <button onClick={() => setSelectedComplaint(null)}>
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AgentDashboard;
