import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { userAPI } from '../services/api';
import './AuthPages.css';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Try user login first
      const response = await userAPI.login(formData);
      const { user } = response.data;

      localStorage.setItem('user', JSON.stringify(user));

      if (user.role === 'MANAGER') {
        navigate('/manager/dashboard');
      } else {
        navigate(`/user/dashboard/${user.userId}`);
      }
    } catch (userError) {
      // If user login fails, try agent login
      try {
        const agentResponse = await fetch(`http://localhost:8080/api/agent/login?email=${formData.email}&password=${formData.password}`);

        if (agentResponse.ok) {
          const agentData = await agentResponse.json();
          localStorage.setItem('agentData', JSON.stringify(agentData));
          navigate('/agent-dashboard');
        } else {
          setError('Invalid email or password');
          alert('Invalid email or password');
        }
      } catch (agentError) {
        setError('Login failed. Please try again.');
        alert('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <h1>Complaint Management System</h1>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
          {error && <div className="error">{error}</div>}
        </form>
        <div className="auth-link">
          <p>Don't have an account? <a href="/signin">Sign Up</a></p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
