import React, { useEffect } from 'react';

const HomePage = () => {
  useEffect(() => {
    // Apply the CSS styles directly to the document
    const style = document.createElement('style');
    style.textContent = `
      * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
      }
      body {
        font-family: Arial, sans-serif;
        background: #2c3e50;
        color: white;
      }
      header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 20px 50px;
        background: rgba(0,0,0,0.2);
      }
      .logo {
        font-size: 24px;
        font-weight: bold;
        position: absolute;
        left: 50px;
        top: 20px;
      }
      .auth-buttons {
        display: flex;
        gap: 15px;
        position: absolute;
        right: 50px;
        top: 20px;
      }
      .auth-buttons a {
        padding: 10px 25px;
        text-decoration: none;
        border-radius: 5px;
        font-size: 16px;
        transition: 0.3s;
      }
      .btn-login {
        background: transparent;
        color: white;
        border: 2px solid white;
      }
      .btn-login:hover {
        background: white;
        color: #2c3e50;
      }
      .btn-signin {
        background: white;
        color: #2c3e50;
      }
      .btn-signin:hover {
        background: #ecf0f1;
      }
      .hero {
        text-align: center;
        padding: 100px 20px;
      }
      .hero h1 {
        font-size: 48px;
        margin-bottom: 20px;
      }
      .hero p {
        font-size: 20px;
        color: #ecf0f1;
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <>
      <header>
        <div className="logo">CMS</div>
        <div className="auth-buttons">
          <a href="/faq" className="btn-login" style={{marginRight: '10px'}}>FAQ</a>
          <a href="/login" className="btn-login">Login</a>
          <a href="/signin" className="btn-signin">Sign In</a>
        </div>
      </header>
    </>
  );
};

export default HomePage;
