import React from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const navigate = useNavigate();

  const styles = {
    body: {
      margin: 0,
      padding: 0,
      boxSizing: 'border-box',
      fontFamily: 'Arial, sans-serif',
      background: '#2c3e50',
      color: 'white',
      minHeight: '100vh'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '20px 50px',
      background: 'rgba(0,0,0,0.2)'
    },
    logo: {
      fontSize: '24px',
      fontWeight: 'bold'
    },
    authButtons: {
      display: 'flex',
      gap: '15px'
    },
    btnLogin: {
      padding: '10px 25px',
      textDecoration: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      transition: '0.3s',
      background: 'transparent',
      color: 'white',
      border: '2px solid white',
      display: 'inline-block'
    },
    btnSignin: {
      padding: '10px 25px',
      textDecoration: 'none',
      borderRadius: '5px',
      fontSize: '16px',
      transition: '0.3s',
      background: 'white',
      color: '#2c3e50',
      display: 'inline-block'
    },
    hero: {
      textAlign: 'center',
      padding: '100px 20px'
    },
    heroH1: {
      fontSize: '48px',
      marginBottom: '20px',
      margin: '0 0 20px 0'
    },
    heroP: {
      fontSize: '20px',
      color: '#ecf0f1',
      margin: 0
    }
  };

  return (
    <div style={styles.body}>
      <header style={styles.header}>
        <div style={styles.logo}>CMS</div>
        <div style={styles.authButtons}>
          <a 
            href="/faq"
            style={{...styles.btnLogin, marginRight: '10px'}}
            onMouseEnter={(e) => {
              e.target.style.background = 'white';
              e.target.style.color = '#2c3e50';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'transparent';
              e.target.style.color = 'white';
            }}
          >FAQ</a>

          <a 
            href="/login"
            style={styles.btnLogin}
            onMouseEnter={(e) => {
              e.target.style.background = 'white';
              e.target.style.color = '#2c3e50';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'transparent';
              e.target.style.color = 'white';
            }}
          >Login</a>

          <a 
            href="/signin"
            style={styles.btnSignin}
            onMouseEnter={(e) => {
              e.target.style.background = '#ecf0f1';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'white';
            }}
          >Sign In</a>
        </div>
      </header>

      <div style={styles.hero}>
        <h1 style={styles.heroH1}>Complaint Management System</h1>
        <p style={styles.heroP}>Efficiently manage and track all your complaints in one place</p>
      </div>
    </div>
  );
};

export default HomePage;
